
public class AverageNumber { //class

	public static void main(String[] args) { //main method
		// TODO Auto-generated method stub
		int num1=20; //declare variable and value
		int num2=30; 
		int num3=10; 
		double avg; 
		
		avg=(num1+num2+num3)/3; //equation for average
		System.out.println("Average of three numbers is " + avg); //print average
	

	}

}
