
public class MultiplyInteger {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1=542;
		int num2=num1%10;
		int num3=542/10;
		int num4=num3%10;
		int num5=54/10;
		int num6=num5%10;
		
		int multiply = num2*num4*num6;
		System.out.println("The multiplication of all digits in 542 is "+ multiply);
		

	}

}
